sp_widget.client_script:
function($scope,$rootScope) {
  /* widget controller */
  var c = this;
	$rootScope.$on('selectNote',function(event,data){
		console.log("event listener caught rec: " + JSON.stringify($rootScope.noteID));
		c.server.get({
			action: 'getNote',
			noteID: $rootScope.noteID
		}).then(function(r){
			c.data.noteID = r.data.note.sys_id;
			c.data.title = r.data.note.title;
			c.data.note = r.data.note.note;
			console.log(JSON.stringify(c.data));
		});
	})

	c.updateNote = function(type){
		c.server.get({
			action: 'updateNote',
			noteID: c.data.noteID,
			noteTitle: c.data.title,
			noteBody: c.data.note

		}).then(function(r){

			$rootScope.$emit('updateListNote')
		})


	}

}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	if(input && input.noteID){
		var noteGr = new GlideRecord('x_snc_createnotes_note');
		if(noteGr.get(input.noteID)){
			if(input.action == 'getNote'){
				data.note = {};
				$sp.getRecordValues(data.note, noteGr, 'sys_id,title,note')

			}
			else if(input.action == 'updateNote'){
				noteGr.title = input.noteTitle;
				noteGr.note = input.noteBody;
				console.log("before updating "+ input.noteTitle +" " + input.noteBody)
				noteGr.update();

			}
		}
	}

})();
